<?php// No direct access to this filedefined('_JEXEC') or die('Restricted access');// import Joomla view libraryjimport('joomla.application.component.view');jimport('joomla.form.helper');JHtml::addIncludePath(JPATH_PLATFORM . '/helpers/html');/** * Attendance Event View */class attendanceViewResponseDetails extends JViewLegacy {	/**	 * display method of event view	 * @return void	 */	public function display($tpl = null) {		// get the Data		$item = $this -> get('Item');		$this -> state = $this -> get('State');		require_once JPATH_COMPONENT . '/helpers/attendance.php';		// Check for errors.		if (count($errors = $this -> get('Errors'))) {			JError::raiseError(500, implode('<br />', $errors));			return false;		}		//Following variables used more than once		$this -> sortColumn = $this -> state -> get('list.ordering');		$this -> sortDirection = $this -> state -> get('list.direction');		$this -> searchterms = $this -> state -> get('filter.search');		$this -> groupby = $this -> state -> get('list.groupby');		$this -> FilterType = $this -> state -> get('filter.filter_type');		$this -> FilterId = $this -> state -> get('filter.filter_id');		$this -> event_user = $this -> state -> get('filter.event_user');		$this -> list_view = $this -> state -> get('list.list_view');		// Assign the Data		$this -> item = $item;		// Display the template		parent::display($tpl);	}}